-- ************************************** "phlogiston_one_phase_result"

CREATE TABLE "phlogiston_one_phase_result"
(
    "id"                        bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "create_date"               timestamp with time zone NOT NULL,
    "user_login"                varchar(30) NOT NULL,
    "pressure"                  DOUBLE PRECISION,
    "temperature"               DOUBLE PRECISION,
    "specific_volume"           DOUBLE PRECISION,
    "density"                   DOUBLE PRECISION,
    "specific_entropy"          DOUBLE PRECISION,
    "specific_enthalpy"         DOUBLE PRECISION,
    "specific_internal_energy"  DOUBLE PRECISION,
    "specific_heat_capacity_p"  DOUBLE PRECISION,
    "specific_heat_capacity_v"  DOUBLE PRECISION
);
